import requests
from subprocess import check_output
a = requests.post("http://www.bruxellesmobilite.irisnet.be/urbis/geoserver/wfs",data="""<wfs:GetFeature xmlns:wfs="http://www.opengis.net/wfs" service="WFS" version="1.0.0" xsi:schemaLocation="http://www.opengis.net/wfs http://schemas.opengis.net/wfs/1.0.0/WFS-transaction.xsd" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance"><wfs:Query typeName="feature:STIB_STOP_GEO" xmlns:feature="http://mobility.irisnet.be"><ogc:Filter xmlns:ogc="http://www.opengis.net/ogc"><ogc:BBOX><ogc:PropertyName>GEOM</ogc:PropertyName><gml:Box xmlns:gml="http://www.opengis.net/gml" srsName="EPSG:31370"><gml:coordinates decimal="." cs="," ts=" ">047033.95140118,063110.89946181 254971.46727622,270455.74748485</gml:coordinates></gml:Box></ogc:BBOX></ogc:Filter></wfs:Query></wfs:GetFeature>""")
open("/tmp/points","w").write(a.text)
check_output(["ogr2ogr", "-f", "GeoJSON", "-t_srs", "epsg:4326", "-s_srs", "epsg:31370", "/tmp/points.json","/tmp/points"])